using System.Runtime.Serialization;

namespace PopMedNet.Dns.DTO.QueryComposer
{
    /// <summary>
    /// DTO for the Manifest.json that is generated by the routing controller.
    /// </summary>
    [DataContract]
    public class DistributedRegressionManifestFile
    {
        /// <summary>
        /// A List of items specifing information about items 
        /// </summary>
        [DataMember]
        public IEnumerable<DistributedRegressionAnalysisCenterManifestItem> Items { get; set; }
        /// <summary>
        /// A List of items specifing information about the DataPartners
        /// </summary>
        [DataMember]
        public IEnumerable<DistributedRegressionManifestDataPartner> DataPartners { get; set; }

    }

    /// <summary>
    /// A manifest item specifing information about an input document for the Analysis Center.
    /// </summary>
    [DataContract]
    public class DistributedRegressionAnalysisCenterManifestItem
    {
        /// <summary>
        /// Gets or sets the DocumentID.
        /// </summary>
        [DataMember]
        public Guid DocumentID { get; set; }
        /// <summary>
        /// Gets or sets the document revisionset ID.
        /// </summary>
        [DataMember]
        public Guid RevisionSetID { get; set; }
        /// <summary>
        /// Gets or sets the ID of the response associated with the document.
        /// </summary>
        [DataMember]
        public Guid ResponseID { get; set; }
        /// <summary>
        /// Gets or sets the ID of the datamart.
        /// </summary>
        [DataMember]
        public Guid DataMartID { get; set; }
        /// <summary>
        /// Gets or sets the folder identifier for the datamart.
        /// </summary>
        [DataMember]
        public string DataPartnerIdentifier { get; set; }
        /// <summary>
        /// Gets or sets the name of the datamart.
        /// </summary>
        [DataMember]
        public string DataMart { get; set; }
        /// <summary>
        /// Gets or sets the routing ID.
        /// </summary>
        [DataMember]
        public Guid RequestDataMartID { get; set; }        
    }

    /// <summary>
    /// Information about the DataPartners
    /// </summary>
    [DataContract]
    public class DistributedRegressionManifestDataPartner
    {
        /// <summary>
        /// The Identifier of the Datamart
        /// </summary>
        [DataMember]
        public Guid DataMartID { get; set; }
        /// <summary>
        /// The Routing type of the Datamart.  AC or DP
        /// </summary>
        [DataMember]
        public Enums.RoutingType RouteType { get; set; }
        /// <summary>
        /// The Identifier of the DataMart.
        /// </summary>
        [DataMember]
        public string DataMartIdentifier { get; set; }
        /// <summary>
        /// The Code of the DataMart.
        /// </summary>
        [DataMember]
        public string DataMartCode { get; set; }
    }
}
